namespace Sparsh\VideoGallery\Controller\Adminhtml\Video;

use Magento\Backend\App\Action;
use Magento\TestFramework\ErrorLog\Logger;
use Magento\Framework\Filesystem\Driver\File;

/**
 * PHP version 7.1
 *
 * @category  Sparsh
 * @package   Sparsh_VideoGallery
 * @author    Sparsh <magento@sparsh-technologies.com>
 * @copyright 2019 This file was generated by Sparsh
 * @license   https://www.sparsh-technologies.com  Open Software License (OSL 3.0)
 * @link      https://www.sparsh-technologies.com
 */

class Delete extends \Magento\Backend\App\Action
{

    protected $videoLoader;

    protected $fileSystem;
    /**
     * @var File
     */
    private $file;

    /**
     * Delete constructor.
     * @param \Magento\Backend\App\Action\Context $context
     * @param \Sparsh\VideoGallery\Model\Video $videoLoader
     * @param \Magento\Framework\Filesystem $fileSystem
     * @param File $file
     */
    public function __construct(
        \Magento\Backend\App\Action\Context $context,
        \Sparsh\VideoGallery\Model\Video $videoLoader,
        \Magento\Framework\Filesystem $fileSystem,
        File $file
    ) {
        parent::__construct($context);
        $this->videoLoader = $videoLoader;
        $this->fileSystem = $fileSystem;
        $this->file = $file;
    }

    /**
     * {@inheritdoc}
     *
     * @return NUll
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Sparsh_VideoGallery::delete');
    }

    /**
     * Delete action
     *
     * @return \Magento\Framework\Controller\ResultInterface
     */
    public function execute()
    {
        $id = $this->getRequest()->getParam('id');
        /**
         * Delete action
         *
         * @var \Magento\Backend\Model\View\Result\Redirect $resultRedirect
         */
        $resultRedirect = $this->resultRedirectFactory->create();
        if ($id) {
            try {
                $model = $this->videoLoader;
                $data = $model->load($id);
                //Delete Image From folder
                $filename = $data->getFile();
                $fileSystem = $this->fileSystem;
                $mediaPath = $fileSystem
                    ->getDirectoryRead(\Magento\Framework\App\Filesystem\DirectoryList::MEDIA)
                    ->getAbsolutePath('/sparsh/video_gallery/');
                if ($filename && $this->file->isExists($mediaPath . $filename)) {
                    $this->file->deleteFile($mediaPath . $filename);
                }

                $model->delete();
                $this->messageManager->addSuccess(__('The Video has been deleted.'));
                return $resultRedirect->setPath('*/*/');
            } catch (\Exception $e) {
                $this->messageManager->addError($e->getMessage());
                return $resultRedirect->setPath('*/*/edit', ['id' => $id]);
            }
        }
        $this->messageManager->addError(__('We can\'t find a Video to delete.'));
        return $resultRedirect->setPath('*/*/');
    }
}
