namespace Sparsh\VideoGallery\Controller\Adminhtml\Video;

use Magento\Backend\App\Action;

/**
 * PHP version 7.1
 *
 * @category  Sparsh
 * @package   Sparsh_VideoGallery
 * @author    Sparsh <magento@sparsh-technologies.com>
 * @copyright 2019 This file was generated by Sparsh
 * @license   https://www.sparsh-technologies.com  Open Software License (OSL 3.0)
 * @link      https://www.sparsh-technologies.com
 */

class Edit extends \Magento\Backend\App\Action
{
    /**
     * Core registry
     *
     * @var \Magento\Framework\Registry
     */
    protected $coreRegistry = null;

    /**
     * Resultfactory
     *
     * @var \Magento\Framework\View\Result\PageFactory
     */
    protected $resultPageFactory;

    protected $videoLoader;

    protected $sessionModel;

    /**
     * Initialize
     *
     * @param Action\Context $context           Initialize Context
     * @param PageFactory    $resultPageFactory Initialize resultfactory
     * @param Registry       $registry          Initialize registry
     */
    public function __construct(
        Action\Context $context,
        \Magento\Framework\View\Result\PageFactory $resultPageFactory,
        \Magento\Framework\Registry $registry,
        \Sparsh\VideoGallery\Model\Video $videoLoader,
        \Magento\Backend\Model\Session $sessionModel
    ) {
        $this->resultPageFactory = $resultPageFactory;
        $this->coreRegistry = $registry;
        $this->videoLoader = $videoLoader;
        $this->sessionModel = $sessionModel;
        parent::__construct($context);
    }

    /**
     * {@inheritdoc}
     *
     * @return NUll
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Sparsh_VideoGallery::save');
    }

    /**
     * Init actions
     *
     * @return \Magento\Backend\Model\View\Result\Page
     */
    protected function _initAction()
    {
        /**
         * Load layout, set active menu and breadcrumbs
         *
         * @var \Magento\Backend\Model\View\Result\Page $resultPage
         */
        $resultPage = $this->resultPageFactory->create();
        $resultPage->setActiveMenu('Sparsh_VideoGallery::Video')
            ->addBreadcrumb(__('Video Gallery'), __('Video Gallery'))
            ->addBreadcrumb(__('Manage Video Details'), __('Manage Video Details'));
        return $resultPage;
    }

    /**
     * Edit Video post
     *
     * @return \Magento\Backend\Model\View\Result\Page|\Magento\Backend\Model\View\Result\Redirect
     */
    public function execute()
    {
        $id = $this->getRequest()->getParam('id');
        $model = $this->videoLoader;

        if ($id) {
            $model->load($id);
            if (!$model->getId()) {
                $this->messageManager->addError(__('This post no longer exists.'));
                /**
 * \Magento\Backend\Model\View\Result\Redirect $resultRedirect
*/
                $resultRedirect = $this->resultRedirectFactory->create();

                return $resultRedirect->setPath('*/*/');
            }
        }

        $data = $this->sessionModel->getFormData(true);
             
        if (!empty($data)) {
            $model->setData($data);
        }

        $this->coreRegistry->register('videogallery_video', $model);
        
        $resultPage = $this->_initAction();
        $resultPage->addBreadcrumb(
            $id ? __('Edit Video Detail') : __('New Video Detail'),
            $id ? __('Edit Video Detail') : __('New Video Detail')
        );
        $resultPage->getConfig()->getTitle()->prepend(__('Videos'));
        $resultPage->getConfig()->getTitle()
            ->prepend($model->getId() ? $model->getTitle() : __('New Video Detail'));

        return $resultPage;
    }
}
