class Index extends \Magento\Backend\App\Action
{
    protected $resultPageFactory = false;

    /**
     * Initialize
     *
     * @param Action\Context $context           Initialize Context
     * @param PageFactory    $resultPageFactory Initialize resultPageFactory
     */
    public function __construct(
        \Magento\Backend\App\Action\Context $context,
        \Magento\Framework\View\Result\PageFactory $resultPageFactory
    ) {
        parent::__construct($context);
        $this->resultPageFactory = $resultPageFactory;
    }

    /**
     * Return Title
     *
     * @return VideoTitle
     */
    public function execute()
    {
        $resultPage = $this->resultPageFactory->create();
        $resultPage->setActiveMenu('Sparsh_VideoGallery::Video');
        $resultPage->addBreadcrumb(__('Video Gallery'), __('Video Gallery'));
        $resultPage->getConfig()->getTitle()->prepend((__('Video Gallery')));

        return $resultPage;
    }
}
