namespace Sparsh\VideoGallery\Controller\Adminhtml\Video;

use Magento\Backend\App\Action\Context;
use Magento\Framework\Filesystem\Driver\File;
use Magento\Ui\Component\MassAction\Filter;
use Sparsh\VideoGallery\Model\ResourceModel\Video\CollectionFactory;
use Magento\Framework\Filesystem;
use Magento\Framework\Controller\ResultFactory;

/**
 * PHP version 7.1
 *
 * @category  Sparsh
 * @package   Sparsh_VideoGallery
 * @author    Sparsh <magento@sparsh-technologies.com>
 * @copyright 2019 This file was generated by Sparsh
 * @license   https://www.sparsh-technologies.com  Open Software License (OSL 3.0)
 * @link      https://www.sparsh-technologies.com
 */

class MassDelete extends \Magento\Backend\App\Action
{
    /**
     * Filter variable
     *
     * @var Filter
     */
    protected $filter;

    /**
     * CollectionFactory variable
     *
     * @var CollectionFactory
     */
    protected $collectionFactory;

    /**
     * FileSystem variable
     *
     * @var FileSystem
     */
    protected $fileSystem;
    /**
     * @var File
     */
    private $file;

    /**
     * Initialize
     *
     * @param Context $context Initialize context
     * @param Filter $filter Initialize filter
     * @param CollectionFactory $collectionFactory Initialize Collectionfactory
     * @param Filesystem $fileSystem
     * @param File $file
     */
    public function __construct(
        Context $context,
        Filter $filter,
        CollectionFactory $collectionFactory,
        Filesystem $fileSystem,
        File $file
    ) {
        $this->filter = $filter;
        $this->collectionFactory = $collectionFactory;
        $this->fileSystem = $fileSystem;
        parent::__construct($context);
        $this->file = $file;
    }
    /**
     * Execute action
     *
     * @return \Magento\Backend\Model\View\Result\Redirect
     * @throws \Magento\Framework\Exception\LocalizedException|\Exception
     */
    public function execute()
    {
        $collection = $this->filter
            ->getCollection($this->collectionFactory->create());
        $collectionSize = $collection->getSize();

        foreach ($collection as $item) {
            //Delete Image From folder
            $filename = $item->getFile();
            $fileSystem = $this->fileSystem;
            $mediaPath = $fileSystem
                ->getDirectoryRead(\Magento\Framework\App\Filesystem\DirectoryList::MEDIA)
                ->getAbsolutePath('/sparsh/video_gallery/');
            if ($filename && $this->file->isExists($mediaPath . $filename)) {
                $this->file->deleteFile($mediaPath . $filename);
            }
            $item->delete();
        }

        $this->messageManager->addSuccess(
            __(
                'A total of %1 record(s) have been deleted.',
                $collectionSize
            )
        );

        /**
         * Redirect to page
         *
         * @var \Magento\Backend\Model\View\Result\Redirect $resultRedirect
         */
        $resultRedirect = $this->resultFactory->create(ResultFactory::TYPE_REDIRECT);
        return $resultRedirect->setPath('*/*/');
    }
}
